settings.custom_liquid:
{% comment %}
  FAQs Page Template
  - Displays FAQs dynamically from Shopify Metafields (if available)
  - Supports collapsible answers for a cleaner UI
  - Includes a search bar for better user experience
{% endcomment %}

<div class="faq-container">
  <h1>Frequently Asked Questions</h1>

  <!-- Search Bar -->
  <div class="faq-search">
    <input type="text" id="faqSearch" placeholder="Search for answers..." onkeyup="filterFAQs()">
  </div>

  <!-- FAQ Sections -->
  <div class="faq-sections">

    <!-- Orders Section -->
    <div class="faq-section">
      <h2>Orders</h2>
      <div class="faq-item">
        <button class="faq-question">How do I track my order? <span class="arrow">▼</span></button>
        <div class="faq-answer">You can track your order using the tracking link sent to your email.</div>
      </div>
      <div class="faq-item">
        <button class="faq-question">Can I modify or cancel my order? <span class="arrow">▼</span></button>
        <div class="faq-answer">Once an order is placed, modifications may not be possible. Please contact support.</div>
      </div>
    </div>

    <!-- Shipping Section -->
    <div class="faq-section">
      <h2>Shipping & Delivery</h2>
      <div class="faq-item">
        <button class="faq-question">What are the shipping charges? <span class="arrow">▼</span></button>
        <div class="faq-answer">We offer free shipping for orders above $50. Below that, standard rates apply.</div>
      </div>
      <div class="faq-item">
        <button class="faq-question">How long does delivery take? <span class="arrow">▼</span></button>
        <div class="faq-answer">Standard shipping takes 5-7 business days. Express shipping is available at checkout.</div>
      </div>
    </div>

    <!-- Returns & Exchanges -->
    <div class="faq-section">
      <h2>Returns & Exchanges</h2>
      <div class="faq-item">
        <button class="faq-question">What is your return policy? <span class="arrow">▼</span></button>
        <div class="faq-answer">You can return items within 30 days if they are unused and in original packaging.</div>
      </div>
      <div class="faq-item">
        <button class="faq-question">How do I request a return? <span class="arrow">▼</span></button>
        <div class="faq-answer">Contact our support team at <a href="mailto:trezon.in@gmail.com">trezon.in@gmail.com</a>.</div>
      </div>
    </div>

    <!-- Payments Section -->
    <div class="faq-section">
      <h2>Payments</h2>
      <div class="faq-item">
        <button class="faq-question">What payment methods do you accept? <span class="arrow">▼</span></button>
        <div class="faq-answer">We accept Visa, MasterCard, PayPal, and Apple Pay.</div>
      </div>
      <div class="faq-item">
        <button class="faq-question">Is my payment secure? <span class="arrow">▼</span></button>
        <div class="faq-answer">Yes, we use SSL encryption to ensure safe transactions.</div>
      </div>
    </div>
  </div>
</div>

<!-- CSS for Styling -->
<style>
  body {
    font-family: Arial, sans-serif;
    background: #f8f8f8;
    margin: 0;
    padding: 0;
  }

  .faq-container {
    max-width: 900px;
    margin: 40px auto;
    padding: 20px;
    background: white;
    border-radius: 10px;
    box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.1);
  }

  h1 {
    text-align: center;
    margin-bottom: 20px;
  }

  .faq-search {
    text-align: center;
    margin-bottom: 20px;
  }

  .faq-search input {
    width: 80%;
    padding: 10px;
    border: 1px solid #ddd;
    border-radius: 5px;
  }

  .faq-sections {
    margin-top: 20px;
  }

  .faq-section {
    margin-bottom: 30px;
    padding: 15px;
    border-radius: 8px;
    background: #FFF9E1; /* Light warm background for sections */
  }

  .faq-section h2 {
    border-bottom: 2px solid #000;
    padding-bottom: 5px;
  }

  .faq-item {
    margin: 10px 0;
  }

  .faq-question {
    width: 100%;
    text-align: left;
    padding: 12px;
    background: white;
    border: 1px solid #ddd;
    cursor: pointer;
    font-size: 16px;
    border-radius: 5px;
    display: flex;
    justify-content: space-between;
    align-items: center;
  }

  .faq-answer {
    display: none;
    padding: 10px;
    margin-top: 5px;
    background: #f9f9f9;
    border-radius: 5px;
  }

  .faq-question .arrow {
    font-size: 14px;
    transition: transform 0.2s;
  }

  .faq-question.active .arrow {
    transform: rotate(180deg);
  }
</style>

<!-- JavaScript for FAQ Collapsible Feature -->
<script>
  document.querySelectorAll('.faq-question').forEach(button => {
    button.addEventListener('click', function() {
      this.classList.toggle('active');
      let answer = this.nextElementSibling;
      answer.style.display = answer.style.display === 'block' ? 'none' : 'block';
    });
  });

  function filterFAQs() {
    let input = document.getElementById("faqSearch").value.toLowerCase();
    let faqs = document.querySelectorAll(".faq-item");

    faqs.forEach(item => {
      let text = item.textContent.toLowerCase();
      item.style.display = text.includes(input) ? "block" : "none";
    });
  }
</script>
