settings.custom_liquid:
{% comment %}
  Template: Track Your Order
  This page allows customers to check their order status using their order number and email.
{% endcomment %}

<div class="track-order-container">
  <h1>Track Your Order</h1>
  <p>Enter your order number and email to check your order status.</p>

  <form class="track-order-form">
    <label for="order-number">Order Number</label>
    <input type="text" id="order-number" placeholder="Enter your order number" required>

    <label for="email">Email Address</label>
    <input type="email" id="email" placeholder="Enter your email" required>

    <button type="submit">Track Order</button>
  </form>

  <div id="order-status"></div>

  <p class="help-text">Need help? Contact us at <a href="mailto:trezon.in@gmail.com">trezon.in@gmail.com</a>.</p>
</div>

<style>
  .track-order-container {
    max-width: 600px;
    margin: 0 auto;
    padding: 20px;
    background: #EEDFA4; /* Background color */
    border-radius: 10px;
    text-align: center;
  }
  .track-order-form {
    display: flex;
    flex-direction: column;
    gap: 15px;
    margin-top: 20px;
  }
  .track-order-form label {
    font-weight: bold;
    text-align: left;
    display: block;
  }
  .track-order-form input {
    width: 100%;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-size: 16px;
  }
  .track-order-form button {
    padding: 10px;
    background: #392D00;
    color: white;
    font-size: 16px;
    font-weight: bold;
    border: none;
    border-radius: 5px;
    cursor: pointer;
  }
  .track-order-form button:hover {
    background: #5a4300;
  }
  .help-text {
    margin-top: 20px;
    font-size: 14px;
  }
  .help-text a {
    color: #392D00;
    font-weight: bold;
    text-decoration: none;
  }
  .help-text a:hover {
    text-decoration: underline;
  }
</style>

<script>
  document.querySelector(".track-order-form").addEventListener("submit", function(event) {
    event.preventDefault();
    var orderNumber = document.getElementById("order-number").value.trim();
    var email = document.getElementById("email").value.trim();
    var orderStatusDiv = document.getElementById("order-status");

    if (!orderNumber || !email) {
      orderStatusDiv.innerHTML = "<p style='color: red;'>Please enter both order number and email.</p>";
      return;
    }

    orderStatusDiv.innerHTML = "<p>Checking status for Order #" + orderNumber + "...</p>";

    // Simulated API call (Replace with actual tracking system integration)
    setTimeout(function() {
      orderStatusDiv.innerHTML = "<p style='color: green;'>Your order is currently being processed. Estimated delivery: 3-5 business days.</p>";
    }, 2000);
  });
</script>
